import React from 'react';
import { useState } from 'react';
import '../App.css';
import animals from '../data/cards';

// Math.random to return float number between 0 and 1 // example 0.73
// Math.floor to return int number instead of Float // example 2.97 => 2

const getRandomInt = num => Math.floor(Math.random() * num);
const sortRandom = (a, b) => 0.5 - Math.random();  

let randomIndex = 0;
let randomArr = [];

while (randomIndex < 8)
    {
        let randomNumber = getRandomInt(animals.length);
        if (randomArr.includes(randomNumber)) continue
        else
        {
            randomArr.push(randomNumber);
            randomIndex++;
        }
    }

let eightCards = animals.filter((animal, index) => randomArr.includes(index));
let randomCards = [...eightCards, ...eightCards].sort(sortRandom);

const selected = [];
const matched = [];

const Cards = () => {
    
    const [counter, setCounter] = useState(0);

    const handleCompare = (animal, divID) => {
        if (counter >= 1) 
            {
                setCounter(0);
            }
        else 
            {
            setCounter(counter+1);
            selected.push(animal)
            }
        console.log(counter)
        
        if (selected.length = 2) 
            {
             return (selected[0].id === selected[1].id) ? true : false
            }
        selected.filter( card => card.id)

    }

    return (randomCards.map((animal, divID) =>   
    <div className='card' onClick={() => handleCompare(divID, animal.id)}>
      <div className={(true) ? '': 'hide'}></div>
      <img src={animal.image}></img>
    </div>
    ))
}

export default Cards;
