import '../App.css';
import React from 'react';

const Cards = () => {

let animals = [
    {id: 0, image :"https://images.pexels.com/photos/47547/squirrel-animal-cute-rodents-47547.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 1, image :"https://images.pexels.com/photos/34231/antler-antler-carrier-fallow-deer-hirsch.jpg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 2, image :"https://images.pexels.com/photos/792381/pexels-photo-792381.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 3, image :"https://images.pexels.com/photos/162140/duckling-birds-yellow-fluffy-162140.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 4, image :"https://images.pexels.com/photos/326012/pexels-photo-326012.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 5, image :"https://images.pexels.com/photos/2295744/pexels-photo-2295744.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 6, image :"https://images.pexels.com/photos/86405/penguin-funny-blue-water-86405.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 7, image :"https://images.pexels.com/photos/3608263/pexels-photo-3608263.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 8, image :"https://images.pexels.com/photos/4588065/pexels-photo-4588065.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 9, image :"https://images.pexels.com/photos/357159/pexels-photo-357159.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 10, image :"https://images.pexels.com/photos/59816/swan-bird-nature-water-bird-59816.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 11, image :"https://images.pexels.com/photos/36846/bald-eagle-adler-bird-of-prey-raptor.jpg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 12, image :"https://images.pexels.com/photos/5145/animal-easter-chick-chicken.jpg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 13, image :"https://images.pexels.com/photos/40984/animal-ara-macao-beak-bird-40984.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"}
];


// Math.floor to return int number instead of Float // example 2.97 => 2

// Math.random to return float number between 0 and 1 // example 0.73

// sortRandom to return value in range of [-0.5,0.5] => increase or decrease sort

const getRandomInt = max => Math.floor(Math.random() * max);
const sortRandom = (a, b) => 0.5 - Math.random();  
  
let randomIndex = 0;
let randomArr = [];

while (randomIndex < 8)
    {
        let randomNumber = getRandomInt(14);
        if (randomArr.includes(randomNumber)) continue
        else
        {
            randomArr.push(randomNumber);
            randomIndex++;
        }
    }
    
let eightAnimals = animals.filter((animal, index) => randomArr.includes(index));
    console.log(eightAnimals);
    
let randomAnimals = [...eightAnimals, ...eightAnimals].sort(sortRandom);
    
const [cover, setCover] = usestate("");
const isCovered = (cover) ? "cover" : "";

return (randomAnimals.map(animal =>    
<div className='card'>
  <div className={isCovered}></div>
  <img src={animal.image}></img>
</div>
))

}  
export default Cards
