import React from "react";
import { useState } from "react";
import "../App.css";
import pokemonsCards from "../data/cards";
import cover from "../images/cover.jpg"

// Math.random to return float number between 0 and 1 // example 0.73
// Math.floor to return int number instead of Float // example 2.97 => 2

const Cards = () => {
  const [previousRef, setPreviousRef] = useState();
  const [previousIndex, setPreviousIndex] = useState();

  const handleCompare = (currentCard) => {
    // if (currentCard.status === "show") return true;
    currentCard.status = "card flip";
    if (previousRef) {
      let previousCard = pokemonsCards[previousIndex];
    //   if (currentCard.ref === previousCard.ref)  currentCard.status = previousCard.status = "hide"
      
    //   else   currentCard.status = previousCard.status =  "hide"
      
      setPreviousRef();
      setPreviousIndex();
    } else {

      setPreviousRef(currentCard.ref);
      setPreviousIndex(currentCard.index);

    }
  };

  return pokemonsCards.map((card) => (
    <div key={card.index} className={card.status} onClick={() => handleCompare(card)}>
      <img className = "show" src={card.image}></img>
      <img className = "hide" src={cover}></img>
    </div>
  ));
};

export default Cards;
