import React from 'react';
import { useState } from 'react';
import '../App.css';


const Cards = ({cards}) => {
    
    // const [selected, setSelected] = useState(cards);
    const [previousRef, setPreviousRef] =useState("")
    const [previousIndex, setPreviousIndex] =useState(-1)
    
    const handleCompare = (currentCard) => {
        if (currentCard.status === 'show') return true
        currentCard.status = ''
        if (previousRef)
            {
            currentCard.status = cards[previousIndex].status = (currentCard.ref === previousRef) ? "show" : "hide"
            setPreviousRef(0)
            }
        else    
            {
            setPreviousRef(currentCard.ref)
            setPreviousIndex(currentCard.index)
            }

    }

    return (cards.map((card) =>   
    <div key={card.index} className='card' onClick={() => handleCompare(card)}>
      <div className={card.status}></div>
      <img src={card.image}></img>
    </div>
    ))
}

export default Cards;
