import '../App.css';
import React from 'react';
import { useState } from 'react';

const Cards = () => {

// Section 1 define animals array that consists of animal objects
let animals = [
    {id: 0, cover: true,image :"https://images.pexels.com/photos/47547/squirrel-animal-cute-rodents-47547.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 1, cover: true,image :"https://images.pexels.com/photos/34231/antler-antler-carrier-fallow-deer-hirsch.jpg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 2, cover: true,image :"https://images.pexels.com/photos/792381/pexels-photo-792381.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 3, cover: true,image :"https://images.pexels.com/photos/162140/duckling-birds-yellow-fluffy-162140.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 4, cover: true,image :"https://images.pexels.com/photos/326012/pexels-photo-326012.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 5, cover: true,image :"https://images.pexels.com/photos/2295744/pexels-photo-2295744.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 6, cover: true,image :"https://images.pexels.com/photos/86405/penguin-funny-blue-water-86405.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 7, cover: true,image :"https://images.pexels.com/photos/3608263/pexels-photo-3608263.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 8, cover: true,image :"https://images.pexels.com/photos/4588065/pexels-photo-4588065.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 9, cover: true,image :"https://images.pexels.com/photos/357159/pexels-photo-357159.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 10, cover: true, image :"https://images.pexels.com/photos/59816/swan-bird-nature-water-bird-59816.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 11, cover: true, image :"https://images.pexels.com/photos/36846/bald-eagle-adler-bird-of-prey-raptor.jpg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 12, cover: true, image :"https://images.pexels.com/photos/5145/animal-easter-chick-chicken.jpg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"},
    {id: 13, cover: true, image :"https://images.pexels.com/photos/40984/animal-ara-macao-beak-bird-40984.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"}
];


// Section 2 define required function to get random values
// Math.floor to return int number instead of Float // example 2.97 => 2
// Math.random to return float number between 0 and 1 // example 0.73
// sortRandom to return value in range of [-0.5,0.5] => increase or decrease sort
const getRandomInt = max => Math.floor(Math.random() * max);
const sortRandom = (a, b) => 0.5 - Math.random();  

// Section 3 return random 8 animals objects
let randomIndex = 0;
let randomArr = [];

while (randomIndex < 8)
    {
        let randomNumber = getRandomInt(14);
        if (randomArr.includes(randomNumber)) continue
        else
        {
            randomArr.push(randomNumber);
            randomIndex++;
        }
    }
    
let eightCards = animals.filter((animal, index) => randomArr.includes(index));
    // console.log(eightAnimals);
    
let randomCards = [...eightCards, ...eightCards].sort(sortRandom);

console.log(randomCards);

// let coveredCards = randomCards.filter( card => card.id === )
// Section 4 to define functions to control cards cover using useState

const [cover, setCover] = useState(true);
const isCovered = (cover) ? "cover" : "";

// const handleOnClick = (Element.id) => setCover(false)

console.log(randomCards);

// Section 5 to return cards div
return (randomCards.map(animal =>   
<div className='card' onClick={() => console.log(animal.id ,",,", setCover(false))}>
  <div className={isCovered}></div>
  <img src={animal.image}></img>
</div>
))
}  
export default Cards
