import React from 'react';
import { useState } from 'react';
import '../App.css';
import pokemons from '../data/cards';

// Math.random to return float number between 0 and 1 // example 0.73
// Math.floor to return int number instead of Float // example 2.97 => 2

const getRandomInt = num => Math.floor(Math.random() * num);
let randomIndex = 0;
let randomArr = [];
  
while (randomIndex < 8)
      {
          let randomNumber = getRandomInt(pokemons.length);
          if (randomArr.includes(randomNumber)) continue
          else {   randomArr.push(randomNumber);   randomIndex++;    }
      }

const sortRandom = (a, b) => 0.5 - Math.random();  
let eightCards = pokemons.filter((card, index) => randomArr.includes(index));
let pokemonsCards = [...eightCards, ...eightCards].sort(sortRandom).map((card, index) => Object.assign({index:index}, card))

const Cards = () => {
    
    const [previousRef, setPreviousRef] =useState()
    const [previousIndex, setPreviousIndex] =useState()
    
    const handleCompare = (currentCard) => {
        if (currentCard.status === 'show') return true
        if (!previousIndex) pokemonsCards[previousIndex].status = ''
        currentCard.status  = 'flip'
        // setTimeout(()=>{console.log("timeout !")}, 500);
        if (previousRef)
            {
            currentCard.status = pokemonsCards[previousIndex].status = (currentCard.ref === previousRef) ? "show" : "hide"
            setPreviousRef();
            setPreviousIndex();
            }
        else {  
            setPreviousRef(currentCard.ref); setPreviousIndex(currentCard.index);currentCard.status  = ''; console.log("False!!!")  
        }

    }

    return (pokemonsCards.map((card) =>   
    <div key={card.index} className='card' onClick={() => handleCompare(card)}>
      <div className={card.status}></div>
      <img src={card.image}></img>
    </div>
    ))
}

export default Cards;
