import './App.css';
import Cards from './Components/Cards';
import pokemons from './data/cards';
import { useState } from 'react'
function App() {

  // Math.random to return float number between 0 and 1 // example 0.73
  // Math.floor to return int number instead of Float // example 2.97 => 2

  const getRandomInt = num => Math.floor(Math.random() * num);
  const sortRandom = (a, b) => 0.5 - Math.random();  
  let randomIndex = 0;
  let randomArr = [];

  while (randomIndex < 8)
      {
          let randomNumber = getRandomInt(pokemons.length);
          if (randomArr.includes(randomNumber)) continue
          else {   randomArr.push(randomNumber);   randomIndex++;    }
      }

  let eightCards = pokemons.filter((card, index) => randomArr.includes(index));
  let pokemonsCards = [...eightCards, ...eightCards].map((card, index) => Object.assign({index:index}, card)).sort(sortRandom)

  // const [catchedPokemons, setCatched] = useState()

  return (
    <>
    <div className="main">
     <Cards cards={pokemonsCards} />
    </div>
    <div className='score'>
   </div>
   </>
  );
}

export default App;
