import React from "react";
import { useState } from "react";
import "../App.css";
import pokemonsCards from "../data/cards";

// Math.random to return float number between 0 and 1 // example 0.73
// Math.floor to return int number instead of Float // example 2.97 => 2

const Cards = () => {
  const [previousRef, setPreviousRef] = useState();
  const [previousIndex, setPreviousIndex] = useState();

  const resetCards = (card1, card2) => {
    card1.status = "hide";
    card2.status = "hide";
    setPreviousRef();
    setPreviousIndex();
    console.log("Cards Reset!");
  };
  const handleCompare = (currentCard) => {
    if (currentCard.status === "show") return true;
    currentCard.status = "show";
    if (previousRef) {
      let card1 = pokemonsCards[previousIndex];
      let card2 = currentCard;
      //   currentCard.status = pokemonsCards[previousIndex].status = "show";
      setTimeout(() => resetCards(card1, card2), 2000);
    } else {
      setPreviousRef(currentCard.ref);
      setPreviousIndex(currentCard.index);
    }
  };

  return pokemonsCards.map((card) => (
    <div key={card.index} className="card" onClick={() => handleCompare(card)}>
      <div className={card.status}></div>
      <img src={card.image}></img>
    </div>
  ));
};

export default Cards;
