import React from 'react';
import { useState } from 'react';
import '../App.css';
import animals from '../data/cards';
// import bg from '../images/bg.png'

// Math.random to return float number between 0 and 1 // example 0.73
// Math.floor to return int number instead of Float // example 2.97 => 2

const getRandomInt = num => Math.floor(Math.random() * num);
const sortRandom = (a, b) => 0.5 - Math.random();  

let randomIndex = 0;
let randomArr = [];

while (randomIndex < 8)
    {
        let randomNumber = getRandomInt(animals.length);
        if (randomArr.includes(randomNumber)) continue
        else {   randomArr.push(randomNumber);   randomIndex++;    }
    }

let eightCards = animals.filter((card, index) => randomArr.includes(index));
let cards = [...eightCards, ...eightCards].map((card, index) => Object.assign({index:index}, card))
const Cards = () => {
    
    const [selected, setSelected] = useState(cards);
    const [previous, setPrevious] =useState("")
    const [score, setScore] = useState(0)
    const [className, setClassName] = useState("hide");
    
    const handleCompare = (currentCard) => {
        setClassName('')
        currentCard.status = className
        if (previous)
            {
                if (currentCard.id === previous) 
                    { 
                        console.log("yes Matching")
                    }
                else
                    { 
                        console.log("not Matching")
                        setClassName('hide')
                        selected.forEach( card =>{ 
                            if (card.index === currentCard.index)
                                card.status = className;
                        })
                        setPrevious(0)
                    }
                }
        else
            setPrevious(currentCard.id)
            }

    return (selected.map((card) =>   
    <div key={card.index} className='card' onClick={() => handleCompare(card)}>
      <div className={card.status}></div>
      <img src={card.image}></img>
    </div>
    ))
}

export default Cards;
