import React from 'react';
import { useState } from 'react';
import '../App.css';
import animals from '../data/cards';

const Cards = () => {

// Math.floor to return int number instead of Float // example 2.97 => 2
// Math.random to return float number between 0 and 1 // example 0.73

const getRandomInt = num => Math.floor(Math.random() * num);
const sortRandom = (a, b) => 0.5 - Math.random();  

let randomIndex = 0;
let randomArr = [];

while (randomIndex < 8)
{
    let randomNumber = getRandomInt(animals.length);
    if (randomArr.includes(randomNumber)) continue
    else
    {
        randomArr.push(randomNumber);
        randomIndex++;
    }
}
    
let eightCards = animals.filter((animal, index) => randomArr.includes(index));
let randomCards = [...eightCards, ...eightCards].sort(sortRandom);
let sortedCards = randomCards;

const [position, setPosition] = useState("");

return (sortedCards.map((animal, index) =>   
<div className='card' onClick={() => 
    {
        console.log(index, animal.status);
        setPosition(index);
    }
    }>
  <div className={(position === index) ? '': 'hide'}></div>
  <img src={animal.image}></img>
</div>
))

}
export default Cards;
